55.215000'),(198,'pku acm problem 1699','problem?id=1699','<pre>#include <iostream>\r\n#include <vector>\r\n#include <string>\r\n#include <cstdlib>\r\n#include <algorithm>\r\nusing namespace std;\r\n\r\nint T,  N,  mn, tt, len;\r\nint V[10][10];\r\nvector<string>  s;\r\nbool B[10];\r\n\r\nint dist(string a, string b) {\r\n    for(int i = min(a.size(), b.size()); i >= 0; --i)\r\n        if(b.substr(0, i) == a.substr(a.size()-i))\r\n            return i;\r\n}\r\n\r\nvoid dfs(int v, int d) {\r\n    B[v] = true;\r\n    if(d == N) mn = max(mn, len);\r\n    for(int w = 0; w < N; ++w) {\r\n        if(!B[w]) {\r\n            len += V[v][w];\r\n            dfs(w, d+1);\r\n            len -= V[v][w];\r\n        }\r\n    }\r\n    B[v] = false;\r\n}\r\n\r\nint main() {\r\n    \r\n    for(cin >> T; T--;) {\r\n        cin >> N;\r\n        s.resize(N);\r\n        for(int i = tt = 0; i < N; ++i) {\r\n            cin >> s[i];\r\n            tt += s[i].size();\r\n            for(int j = 0; j < i; ++j) {\r\n                V[i][j] = dist(s[i], s[j]);\r\n                V[j][i] = dist(s[j], s[i]);\r\n            }\r\n        }\r\n        mn = 0;\r\n        for(int i = 0; i < N; ++i) {\r\n            len = 0;\r\n            memset(B, 0, sizeof(B));\r\n            dfs(i, 1);\r\n        }\r\n        cout << tt - mn << endl;\r\n    }\r\n // system(\"pause\");\r\n} \r\n</pre>',1,NULL,'2016-09-29 08:22:55.540000','2016-09-29 08:22:55.540000'),(199,'pku acm problem 2309','problem?id=2309','<pre>#include <cstdio>\r\n#include <cstdlib>\r\n#include <cmath>\r\nint main()\r\n{ int m,x,k,p,q,min,max;\r\n  scanf(\"%d\",&m);\r\n  while(m--)\r\n  { scanf(\"%d\",&x);\r\n    if(x%2==1) \r\n	{ min=x; max=x;\r\n	}\r\n	else\r\n	{ p=1;\r\n	  for(k=1;k<31;k++)\r\n	  { p=p*2; q=p*2;\r\n	    if(x%p==0&&x%q!=0) \r\n			break;\r\n	  }\r\n	  min=x-p+1; \r\n	  max=x+p-1;\r\n	}\r\n	printf(\"%d %d\\n\",min,max);\r\n  }\r\n  system(\"pause\");\r\n  return 0;\r\n}\r\n</pre>',1,NULL,'2016-09-29 08:22:55.866000','2016-09