def map< string,int,less<string> > Smap;\r\nint main()\r\n{\r\n           char temp[31];\r\n           int ans=0;\r\n            Smap smap;\r\n            Smap::iterator itr;\r\n          while(gets(temp)!=NULL)\r\n         {\r\n          if(smap.find(temp)==smap.end())\r\n          smap[temp]=1;\r\n          else smap[temp]++;\r\n           ans++;\r\n        }\r\n        for(itr=smap.begin();itr!=smap.end();itr++)\r\n        {\r\n       printf(\"%s %.4f\\n\",(itr->first).c_str(),(100*itr->second)/(double)ans);\r\n         }\r\n}\r\n</pre>',1,NULL,'2016-09-29 08:22:57.539000','2016-09-29 08:22:57.539000'),(205,'pku acm problem 2656','problem?id=2656','<pre>#include \"stdio.h\"\r\nint main()\r\n{\r\n   int n,i,a,b,max,count;\r\n   while(1)\r\n   {\r\n      max=8;\r\n      scanf(\"%d\",&n);\r\n       if(n==0) break;\r\n         for(i=0;i<n;i++)\r\n         {\r\n            scanf(\"%d%d\",&a,&b);\r\n            if(a+b>max)\r\n            {\r\n               max=a+b;\r\n               count=i;\r\n             }  \r\n          }\r\n        if(max==8) printf(\"%d\\n\",0);\r\n        else printf(\"%d\\n\",count+1);\r\n     }     \r\n}</pre>',1,NULL,'2016-09-29 08:22:57.855000','2016-09-29 08:22:57.855000'),(206,'pku acm problem 1201','problem?id=1201','<pre>#include <vector>\r\n#include <list>\r\n#include <map>\r\n#include <set>\r\n#include <deque>\r\n#include <stack>\r\n#include <bitset>\r\n#include <string>\r\n#include <functional>\r\n#include <fstream>\r\n#include <numeric>\r\n#include <utility>\r\n#include <sstream>\r\n#include <fstream>\r\n#include <iostream>\r\n#include <iomanip>\r\n#include <cstdio>\r\n#include <cmath>\r\n#include <cstdlib>\r\n#include <ctime>\r\n#include <algorithm>\r\nusing namespace std;\r\n\r\n#define  MAX 600000\r\n#define  MAXINT 1000000\r\n\r\nstruct EDGE{\r\n   int st,ed,val;\r\n}edge[MAX];\r\n\r\nint dis[MAX],n,mn,mx;\r\n\r\nint bellman_ford()\r\n{\r\n   int i,k;\r\n   \r\n   for(i=mn; i<=mx; ++i)\r\n   dis[i]=-MAXINT;\r\n   dis[mn]=0;\r\n   bool over;\r\n   for(k=0; k<=mx-mn; ++k)\r\n   {\r\n            over =true;\r\n            for(i=0; i<n; ++i)\r\n            if(dis[edge[i].st]!=-MAXINT&&dis[edge[i].st]+edge[i].val>dis[edge[i].ed])\r\n            {\r\n             dis[edge[i].ed]=dis[edge[i].st]+edge[i].val;\r\n             over=false;                                                                      \r\n             }\r\n             \r\n             for(i=mn; i<mx; ++i)\r\n             if(dis[i]!=-MAXINT&&dis[i]>dis[i+1])\r\n             {\r\n                dis[i+1]=dis[i];\r\n                over=false;                                 \r\n             }\r\n             \r\n             for(i=mx; i>mn; --i)\r\n             if(dis[i]!=-MAXINT&&dis[i]-1>dis[i-1])\r\n             {\r\n               dis[i-1]=dis[i]-1;\r\n               over=false;                                    \r\n             }\r\n             \r\n             if(over)  break;\r\n            } \r\n            \r\n            return dis[mx];\r\n    } \r\n\r\nint main(){\r\n    \r\n    int i;\r\n    \r\n    while(scanf(\"%d\",&n)!=EOF){\r\n                                \r\n                                mn=MAXINT;\r\n                                mx=0;\r\n                                for(i=mx; i<n; i++){\r\n                                          \r\n                                          scanf(\"%d%d%d\",&edge[i].st,&edge[i].ed,&edge[i].val);\r\n                                          edge[i].ed++;\r\n                                          if(edge[i].ed>mx)\r\n                                            mx=edge[i].ed;\r\n                                          if(edge[i].st<mn)\r\n                                            mn=edge[i].st;  \r\n                                          }\r\n                                          printf(\"%d\\n\",bellman_ford());\r\n                                }\r\n                   return 0;             \r\n    }\r\n\r\n</pre>',1,NULL,'2016-09-29 08:22:58.185000','2016-09-29 08:22:58.185000'),(207,'pku acm problem 1163','problem?id=1163','<pre>#include \"stdio.h\"\r\nint main()\r\n{\r\n int i,j,n,max;\r\n int a[100][100];\r\n scanf(\"%d\",&n);\r\n for(i=0;i<n;i++)\r\n  for(j=0;j<=i;j++)\r\n  scanf(\"%d\",&a[i][j]);\r\n  max=a[0][0];\r\n  for(i=0;i<n-1;i++)\r\n   for(j=0;j<=i;j++)\r\n  {\r\n   if(j==0)\r\n   a[i+1][j]=a[i+1][j]+a[i][j];\r\n   if(j==i)\r\n   a[i+1][j+1]=a[i+1][j+1]+a[i][j];\r\n   }\r\n   for(i=0;i<n-1;i++)\r\n   for(j=0;j<=i-1;j++)\r\n   {\r\n   if(a[i][j]>a[i][j+1])\r\n   max=a[i][j];\r\n   else\r\n   max=a[i][j+1];\r\n   a[i+1][j+1]=a[i+1][j+1]+max;\r\n   }\r\n   i=n-1;\r\n   max=a[i][0];\r\n   for(j=0;j<=i;j++)\r\n   {\r\n    if(max<a[i][j])\r\n    max=a[i][j];\r\n    }\r\n   printf(\"%d\",max);\r\n}      \r\n    </pre>',1,NULL,'2016-09-29 08:22:58.509000','2016-09-29 08:22:58.509000'),(208,'pku acm problem 1160','problem?id=1160','<pre>#include <iostream>\r\n#include <cstdlib>\r\nusing namespace std;\r\nconst int maxint = 0x7FFFFFFF;\r\ntypedef long long int64;\r\nconst int64 maxint64 = 0x7FFFFFFFFFFFFFFFLL;\r\nconst int maxn = 310;\r\n\r\nint n, p;\r\n\r\nint min_cost[maxn][maxn];\r\nint f[maxn][maxn];\r\nint a[maxn];\r\n\r\nint main()\r\n{\r\n    scanf(\"%d%d\", &n, &p);\r\n    for (int i = 1; i <= n; i++)\r\n        scanf(\"%d\", &a[i]);\r\n    \r\n    for (int i = 1; i <= n; i++)\r\n        for (int j = i; j <= n; j++)\r\n        {\r\n            min_cost[i][j] = 0;\r\n            int mid = (i + j) / 2;\r\n            for (int k = i; k <= mid;++k)\r\n                min_cost[i][j] += a[mid] - a[k];\r\n            for (int k = mid + 1; k <= j; k++)\r\n                min_cost[i][j] += a[k] - a[mid];\r\n        }\r\n\r\n for (int i = 0; i <= p; i++)\r\n        for (int j = 0; j <= n; j++)\r\n            f[i][j] = maxint;\r\n    \r\n    f[0][0] = 0;\r\n    for (int i = 0; i <= p; i++)\r\n        for (int j = 0; j <= n; j++) if (f[i][j] < maxint)\r\n        {\r\n            for (int k = 1; j + k <= n; k++)\r\n                f[i + 1][j + k] <?= f[i][j] + min_cost[j + 1][j + k];\r\n        }\r\n    printf(\"%d\\n\", f[p][n]);\r\n    system(\"pause\");\r\n    return 0;\r\n}\r\n</pre>',1,NULL,'2016-09-29 08:22:58.838000','2016-09-29 08:22:58.838000'),(209,'pku acm problem 3173','problem?id=3173','<pre>#include <iostream>\r\n#include <cstdlib>\r\nusing namespace std;\r\nint main()\r\n{\r\n    int n,s,i,j,k=0,p;\r\n    int a[20][20];\r\n    cin>>n>>s;\r\n    p=s;\r\n    for(i=0;i<n;++i)\r\n    {\r\n      s=p+i;    k=0;       \r\n      for(j=0;j<n;++j)\r\n      {\r\n          s=s+k;\r\n          while(s>9)\r\n               s=s-9; \r\n          a[i][j]=s;\r\n            k++;\r\n       }\r\n      }  \r\n    for(i=0,k=0;i<n;++i,++k)\r\n    {\r\n        for(j=0;j<2*k;++j)\r\n        cout<<\' \';\r\n        for(j=i;j<n;++j)\r\n        cout<<a[i][j]<<\' \';\r\n        cout<<endl;\r\n    }\r\n    system(\"pause\");\r\n}      \r\n         \r\n\r\n</pre>',1,NULL,'2016-09-29 08:22:59.143000','2016-09-29 08:22:59.143000'),(210,'pku acm problem 1674','problem?id=1674','<pre>#include <iostream>\r\n#include <cstdlib>\r\nusing namespace std;\r\n\r\nint main()\r\n{\r\n    int t,n,k,count;\r\n    int *data;\r\n    bool flag[10010];\r\n    cin>>t;\r\n    while(t--)\r\n    {\r\n              cin>>n;\r\n              data=new int [n+1];\r\n              for(int i=1; i<n+1; ++i)\r\n              {\r\n                  cin>>data[i];\r\n                  flag[i]=false;\r\n              }\r\n              k=1;  count=0;\r\n              while(k<n+1)\r\n              {\r\n                 if(flag[k]==false)\r\n                 {\r\n                  while(data[k]!=k)\r\n                  {\r\n                               swap(data[data[k]],data[k]);\r\n                               flag[data[k]]=true;\r\n                               count++;\r\n                  }\r\n                    flag[data[k]]=true;\r\n                 }\r\n                 k++;\r\n              }              \r\n 