=0)  break;\r\n            min=pow(2.0,31.0)-1;\r\n            for(i=1;i<=1000000;++i)\r\n            {\r\n               a=pow(i,n);\r\n                temp=fabs(b-a);\r\n                if(temp<min)\r\n                    min=temp;\r\n                else \r\n                   {  a=i-1; break; } \r\n                }         \r\n      printf(\"%.lf\\n\",a);\r\n    }\r\n    system(\"pause\");\r\n}         \r\n</pre>',1,NULL,'2016-09-29 08:23:03.757000','2016-09-29 08:23:03.757000'),(224,'pku acm problem 2388','problem?id=2388','<pre>#include \"stdio.h\"\r\nint main()\r\n{\r\n int n,i,j,k;\r\n long a[10005];\r\n scanf(\"%d\",&n);\r\n for(i=0;i<n;i++)\r\n scanf(\"%d\",&a[i]);\r\n for(i=0;i<n;i++)\r\n { \r\n  for(j=i+1;j<n;j++)\r\n  {\r\n     if(a[i]>a[j])\r\n     {\r\n        k=a[i]; a[i]=a[j]; a[j]=k;\r\n      }\r\n   }\r\n  } \r\n  k=n/2;\r\n  printf(\"%ld\",a[k]);         \r\n}</pre>',1,NULL,'2016-09-29 08:23:04.078000','2016-09-29 08:23:04.078000'),(225,'pku acm problem 1095','problem?id=1095','<pre>#include <iostream>\r\n#include <string>\r\n#include <algorithm>\r\nusing namespace std;\r\n\r\nint Count[20];\r\nint sum[20];\r\n\r\nstring make( int n, int N )\r\n{\r\n    if( n == 1  ) return \"X\" ;\r\n    if( N == 0  ) return \"\";\r\n    \r\n    string str = \"X\";\r\n    int Count_N = 0;\r\n    for( int i = 0; i < 19; ++i ){  \r\n        if( ( Count_N + Count[i] * Count[ n - 1 - i ]) < N )\r\n            Count_N +=  Count[i] * Count[ n - 1 - i ];\r\n        else {\r\n            int N1, N2;\r\n            N2 = ( N - Count_N ) % Count[n - i - 1];\r\n            if( N2 == 0 ) N2 = Count[n-i-1];\r\n            N1 = ( N - Count_N - N2 ) / Count[n - i -1 ] + 1 ; \r\n            // left \r\n            if( i != 0 )\r\n                str = \"(\" + make( i , N1 ) +\")\" + str;\r\n            \r\n            // right\r\n            if( ( n-1-i ) != 0)\r\n                str = str + \"(\" +  make( n-i-1, N2 ) + \")\" ;            \r\n            break;\r\n        }\r\n    }\r\n    \r\n\r\n    return str;\r\n}\r\n\r\n\r\nint main()\r\n{\r\n    memset( Count, 0, sizeof( Count));\r\n    memset( sum, 0, sizeof(sum));\r\n    Count[1] = 1; Count[0] = 1;\r\n    Count[2] = 2;\r\n    sum[1] = 1; sum[0] = 1;\r\n    sum[2] = 3;    \r\n    for(int i=3; i<19; i++){\r\n            \r\n      for(int j=0; j<=i-1; j++){             \r\n            Count[i]+=Count[j]*Count[i-j-1];\r\n            }  \r\n      sum[i]=sum[i-1]+Count[i];\r\n      }    \r\n      int a,n;\r\n      while(cin>>a){                  \r\n                    if(a==0)  break;\r\n                    for(int i=1; i<19; i++){\r\n                            if(sum[i]>=a){\r\n                                          n=i;  break;\r\n                                          }                                           \r\n                            }\r\n                            \r\n             string       str= make( n, a-sum[n-1]);\r\n                    \r\n                    cout<<str<<endl;                  \r\n                     }\r\n    system(\"pause\");\r\n }\r\n\r\n</pre>',1,NULL,'2016-09-29 08:23:04.411000','2016-09-29 08:23:04.411000'),(226,'pku acm problem 1306','problem?id=1306','<pre>#include <iostream>\r\n#include <cstdlib>\r\n#include <cstdio>\r\nusing namespace std;\r\nint main()\r\n{\r\n    int n,m;\r\n    double mul1,mul2,mul3,c;\r\n    while(1)\r\n    {\r\n            cin>>n>>m;\r\n            mul1=1; mul2=1; mul3=1;\r\n            if(n==0&&m==0)  break;\r\n            else \r\n            {\r\n                 for(int i=1;i<=n;++i)\r\n                 mul1=(double)mul1*i;\r\n                 for(int i=1;i<=m;++i)\r\n                 mul2=(double)mul2*i;\r\n                 for(int i=1;i<=n-m;++i)\r\n                 mul3=(double)mul3*i;\r\n            }    \r\n            c=(double)mul1/(mul2*mul3);\r\n       printf(\"%d things taken %d at a time is %.lf exactly.\\n\",n,m,c);\r\n        }\r\n        system(\"pause\");\r\n}        \r\n</pre>',1,NULL,'2016-09-29 08:23:04.740000','2016-09-29 08:23:04.740000'),(227,'pku acm problem 1142','problem?id=1142','<pre>#include <iostream>\r\n#include <cstdlib>\r\n#include <cstdio>\r\nusing namespace std; \r\nint sum(int n)\r\n{\r\n    int m,i,j,sum2;\r\n    int ss[32];    \r\n                    j=0;  sum2=0;   m=n;  i=2;\r\n                    while(i*i<=m)\r\n                    {\r\n                             while(m%i==0)\r\n                             {\r\n                               ss[j]=i;  j++;\r\n                                m=m/i;\r\n                             }  \r\n                             ++i;  \r\n                    } \r\n                    if(m!=1&&m!=n)\r\n                    {  \r\n                     ss[j]=m;  j++;\r\n                     }\r\n                  for(i=0;i<j;++i)\r\n                  {\r\n                                  while(ss[i])\r\n                                  {\r\n                                         sum2=sum2+ss[i]%10;\r\n                                         ss[i]=ss[i]/10;\r\n                                  }\r\n                  }   \r\n                  return  sum2;\r\n}           \r\n\r\nint he(int n)\r\n{\r\n    int sum1;\r\n         sum1=0;  \r\n                    while(n)\r\n                    {\r\n                       sum1=sum1+n%10;\r\n                       n=n/10;\r\n                    }\r\n        return sum1;\r\n}             \r\n\r\nint main()\r\n{\r\n    int  i,n,sum1,sum2,m;\r\n    while(cin>>n&&n)\r\n    {\r\n                    n=n+1;\r\n                    sum1=he(n);\r\n                    sum2=sum(n);\r\n                    while(sum2!=sum1)\r\n                    {\r\n                             n=n+1;\r\n                             sum1=he(n);\r\n                            sum2=sum(n);\r\n                    }                     \r\n           cout<<n<<endl;\r\n    }\r\n    system(\"pause\");\r\n}     \r\n</pre>',1,NULL,'2016-09-29 08:23:05.067000','2016-09-29 08:23:05.0